export function CookieBanner() {
    const [isVisible, setIsVisible] = useState(false)

    useEffect(() => {
        const consent = localStorage.getItem("cookie-consent")
        if (!consent) {
            // eslint-disable-next-line react-hooks/set-state-in-effect
            setIsVisible(true)
        }
    }, [])

    const handleAccept = () => {
        localStorage.setItem("cookie-consent", "accepted")
        setIsVisible(false)
    }

    const handleDecline = () => {
        localStorage.setItem("cookie-consent", "declined")
        setIsVisible(false)
    }

    if (!isVisible) return null

    return (
        <div className="fixed bottom-0 left-0 right-0 p-4 bg-background/80 backdrop-blur-md border-t z-50 animate-in slide-in-from-bottom">
            <div className="container mx-auto flex flex-col md:flex-row items-center justify-between gap-4">
                <div className="space-y-1">
                    <h3 className="font-semibold">Vaše soukromí je pro nás důležité</h3>
                    <p className="text-sm text-muted-foreground">
                        Používáme cookies k analýze návštěvnosti a vylepšení vašich zážitků.
                        Vaše data (výdaje) jsou šifrována a nikdo jiný k nim nemá přístup.
                    </p>
                </div>
                <div className="flex gap-2">
                    <Button variant="outline" onClick={handleDecline}>Odmítnout</Button>
                    <Button onClick={handleAccept}>Přijmout vše</Button>
                </div>
                <Button variant="ghost" size="icon" className="absolute top-2 right-2 md:hidden" onClick={handleDecline}>
                    <X className="h-4 w-4" />
                </Button>
            </div>
        </div>
    )
}

import { prisma } from "@/lib/prismadb"

export const POINTS_PER_LEVEL = 1000

export const ACTION_POINTS = {
    ADD_TRANSACTION: 10,
    WEEKLY_GOAL_MET: 50,
    MONTHLY_GOAL_MET: 200,
    STREAK_BONUS: 5, // per day
}

export async function awardPoints(userId: string, points: number) {
    try {
        const userPoints = await prisma.userPoints.upsert({
            where: { userId },
            create: { userId, totalPoints: points, level: 1 },
            update: { totalPoints: { increment: points } },
        })

        // Check for level up
        const newLevel = Math.floor(userPoints.totalPoints / POINTS_PER_LEVEL) + 1

        if (newLevel > userPoints.level) {
            await prisma.userPoints.update({
                where: { userId },
                data: { level: newLevel },
            })
            // In a real app, we would trigger a notification here
            return { levelUp: true, newLevel }
        }

        return { levelUp: false, totalPoints: userPoints.totalPoints }
    } catch (error) {
        console.error("[GAMIFICATION_ERROR]", error)
        return null
    }
}

